angular.module('templates', []).run(['$templateCache', function($templateCache) {$templateCache.put('mailbox/mailbox.html','<div class="row"> <section class="col-sm-3 sidebar"> <ul class="list-unstyled"> <li class="msg-summary-item" ng-repeat="message in $ctrl.messages"> <message-summary message="message"></message-summary> </li> </ul> </section> <section class="col-sm-9"> <message message="$ctrl.message"></message> </section> </div> ');
$templateCache.put('mailbox/message/message.html','<div class="message"> <header> <h1>{{$ctrl.message.subject}}</h1> <p> <label>From:</label> {{$ctrl.message.sender}} </p> <p> <label>Date:</label> {{$ctrl.message.time_sent | date: \'EEE dd MMM, HH:mm\'}}</p> </header> <article> {{$ctrl.message.message}} </article> </div>');
$templateCache.put('mailbox/message/summary.html','<div class="message-anchor"> <a href="#/{{$ctrl.message.uid}}"> <div class="pull-left text-nowrap"> {{$ctrl.message.sender}} </div> <div class="pull-right text-nowrap text-right"> {{$ctrl.message.time_sent | date: \'EEE dd MMM, HH:mm\'}} </div> <div class="clearfix"></div> <p>{{$ctrl.message.subject}}</p> </a> </div>');}]);